@zedub.tgbot.on(InlineQuery)
@check_owner
async def zed_handler(event):
    builder = event.builder
    result = None
    query = event.text
    await zedub.get_me()
    if query.startswith("سورس") and event.query.user_id == zedub.uid:
        buttons = [[Button.url("Source", "https://t.me/ZedThon"), Button.url("Dev", "https://t.me/ZZZZL1L")]]
        if ZEDPIC and ZEDPIC.endswith((".jpg", ".png", ".gif", ".mp4")):
            result = builder.photo(ZEDPIC, text=Zelzal, buttons=buttons, link_preview=False)
        elif ZEDPIC:
            result = builder.document(ZEDPIC,title="zedub",text=Zelzal,buttons=buttons,link_preview=False)
        else:
            result = builder.article(title="zedub",text=Zelzal,buttons=buttons,link_preview=False)
        await event.answer([result] if result else None)
@zedub.zed_cmd(pattern="سورس")
async def repozedub(event):
    if event.fwd_from:
        return
    TG_BOT = Config.TG_BOT_USERNAME
    if event.reply_to_msg_id:
        await event.get_reply_message()
    response = await zedub.inline_query(TG_BOT, "سورس")
    await response[0].click(event.chat_id)
    await event.delete()
